import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {jwtDecode} from 'jwt-decode';
import '../App.css';

const Dashboard = () => {
  const [modules, setModules] = useState([]);
  const [progress, setProgress] = useState(0);
  const [userId, setUserId] = useState(null);

  const fetchPath = async (uid, token) => {
    try {
      const { data } = await axios.get(`http://localhost:5000/api/path/path/${uid}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setModules(data);
      updateProgress(data);
    } catch (err) {
      console.error('Error fetching path:', err);
    }
  };

  const updateProgress = (moduleList) => {
    const completed = moduleList.filter((m) => m.is_completed).length;
    const pct = Math.floor((completed / moduleList.length) * 100);
    setProgress(pct);
  };

  const toggleComplete = async (moduleId) => {
    try {
      const updated = modules.map((mod) =>
        mod.module_id === moduleId
          ? { ...mod, is_completed: !mod.is_completed }
          : mod
      );
      setModules(updated);
      updateProgress(updated);

      // Update backend
      await axios.post(`http://localhost:5000/api/path/toggle`, {
        userId,
        moduleId,
      });
    } catch (err) {
      console.error('Error updating module status:', err);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token);
    setUserId(decoded.id);
    fetchPath(decoded.id, token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  return (
    <div className="app-container">
      <div className="logout-btn" onClick={handleLogout}>Logout</div>
      <h2 style={{ textAlign: 'center' }}>Your Learning Path</h2>
      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }}></div>
      </div>
      <p style={{ textAlign: 'center', marginBottom: '2rem' }}>{progress}% completed</p>

      {modules.map((mod, idx) => (
        <div className={`module-card ${mod.is_completed ? 'completed' : ''}`} key={idx}>
          <div className="module-title">
            <input
              type="checkbox"
              checked={mod.is_completed}
              onChange={() => toggleComplete(mod.module_id)}
              style={{ marginRight: '10px' }}
            />
            {mod.title}
          </div>
          <a
            className="module-link"
            href={mod.content_link}
            target="_blank"
            rel="noopener noreferrer"
          >
            View Resource â†—
          </a>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
