import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import {jwtDecode} from 'jwt-decode';
import '../App.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const handleLogin = async (e) => {
  e.preventDefault();
  setError('');
  try {
    const res = await axios.post('http://localhost:5000/api/auth/login', {
      email,
      password
    });
    const token = res.data.token;
    localStorage.setItem('token', token);

    navigate('/dashboard');  // ðŸ‘ˆ Only dashboard on login
  } catch (err) {
    setError('Invalid credentials');
  }
};


  return (
    <div className="form-container">
      <h2>Login</h2>
      <h1 className="login-title">Welcome to LearnPath ðŸš€</h1>
<p className="login-intro">
  A personalized platform that guides your journey to becoming a skilled developer, analyst, or engineer.
  Select your goal, track your learning modules, and grow consistently.
</p>

      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          required
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          required
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Log In</button>
        {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
      </form>
      <p style={{ marginTop: '1rem', textAlign: 'center' }}>
        Don't have an account? <Link to="/register">Register here</Link>
      </p>
    </div>
  );
};

export default Login;
