import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import '../App.css';
import ModuleCard from '../components/ModuleCard';

const Dashboard = () => {
  const [modules, setModules] = useState([]);
  const [progress, setProgress] = useState(0);

  const fetchPath = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert("Please log in");
        return;
      }

      // ðŸ”“ Decode user ID from token
      const decoded = jwt_decode(token);
      const userId = decoded.id;

      // ðŸ“¦ Fetch path for that user
      const { data } = await axios.get(`http://localhost:5000/api/path/path/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setModules(data);

      // ðŸ“Š Calculate % completed
      const completed = data.filter((m) => m.is_completed).length;
      const pct = Math.floor((completed / data.length) * 100);
      setProgress(pct);
    } catch (err) {
      console.error('Error fetching path:', err);
    }
  };

  useEffect(() => {
    fetchPath();
  }, []);

  return (
    <div className="app-container">
      <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>Your Learning Path</h2>
      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }}></div>
      </div>
      <p style={{ textAlign: 'center', marginBottom: '2rem' }}>{progress}% completed</p>

      {modules.map((mod, index) => (
        <ModuleCard
          key={index}
          title={mod.title}
          link={mod.content_link}
          completed={mod.is_completed}
        />
      ))}
    </div>
  );
};

export default Dashboard;
